sys_variable_value.value:
(function execute(inputs, outputs) {

  try {
  	var credentials = JSON.parse(new x_tesgh_veeam_app.VeeamAppUtil().getVeeamCredentials(inputs.veeamConnectionSysID));

    var r = new sn_ws.RESTMessageV2('x_tesgh_veeam_app.Veeam Server', 'Get Sessions by Backup Job ID');
    r.setStringParameterNoEscape('baseUrl', credentials.baseurl);
    r.setStringParameterNoEscape('token', inputs.token);
    r.setStringParameterNoEscape('backupJobID', inputs.backupJobID);    
    if (inputs.createdAfterFilter) r.setStringParameterNoEscape('createdAfterString', inputs.createdAfterFilter);
    else r.setStringParameterNoEscape('createdAfterString', '');
	r.setMIDServer(credentials.midserver);

    var response = r.execute();

    outputs.http_status = response.getStatusCode();
    outputs.response_body = response.getBody();
  }
  catch(ex) {
    outputs.error_message = ex.message;
  }

})(inputs, outputs);

