sys_variable_value.value:
(function execute(inputs, outputs) {
  if(inputs.responseBody) {
    var jsonBody = JSON.parse(inputs.responseBody);

    outputs.vm_count = -1;
    var vmCount = -1;
    try{
      vmCount = jsonBody.virtualMachines['includes'].length;
    } catch(ex) {
      outputs.error_message = ex.message
    }

    outputs.vm_count = vmCount;
  }
})(inputs, outputs);

