sys_variable_value.value:
(function execute(inputs, outputs) {

  try {    
    var veeamAppUtil = new x_tesgh_veeam_app.VeeamAppUtil();
    var credentials = JSON.parse(veeamAppUtil.getVeeamCredentials(inputs.veeamConnectionSysID));

    var r = new sn_ws.RESTMessageV2('x_tesgh_veeam_app.Veeam Server', 'Get Job by ID');
    r.setStringParameterNoEscape('baseUrl', credentials.baseurl);
    r.setStringParameterNoEscape('token', inputs.token);
    r.setStringParameterNoEscape('id', inputs.jobId);
 	if (credentials.midserver) r.setMIDServer(credentials.midserver);

    var response = r.execute();

    outputs.response_body = response.getBody();
    outputs.http_status = response.getStatusCode();
  }
  catch(ex) {
    outputs.error_message = ex.message;
  }

})(inputs, outputs);

