sys_variable_value.value:
(function execute(inputs, outputs) {

  if (typeof inputs.responseBody == 'undefined') {
    outputs.access_token = '';
  } else {
    var accessToken = '';
    var responseBodyObject = JSON.parse(inputs.responseBody);
    if (responseBodyObject && responseBodyObject.access_token) accessToken = responseBodyObject.access_token;

    outputs.access_token = accessToken;
  }

})(inputs, outputs);

