sys_variable_value.value:
(function execute(inputs, outputs) {

  if (inputs.responseBody) {
	var jsonBody = { "records": [] };
    var jsonResponse = JSON.parse(inputs.responseBody);

    jsonResponse.virtualMachinesIncludes = JSON.stringify(jsonResponse.virtualMachines['includes']);
    jsonResponse.virtualMachinesExcludes = JSON.stringify(jsonResponse.virtualMachines['excludes']);
    jsonResponse.veeamConnection = inputs.veeamConnection;

    jsonBody.records.push(jsonResponse);

    var request = new sn_ws.RESTMessageV2();
    request.setEndpoint(gs.getProperty('glide.servlet.uri') + 'api/now/import/x_tesgh_veeam_app_import_veeam_backup_job/insertMultiple');
    request.setHttpMethod('POST');
    request.setAuthenticationProfile('basic', '39480e731b299910e06d520b234bcb48');
    request.setRequestHeader("Accept","application/json");
    request.setRequestHeader('Content-Type','application/json');
    request.setRequestBody(JSON.stringify(jsonBody));

    var response = request.execute();

    outputs.body = response.getBody();
    outputs.jsonbody = JSON.stringify(jsonBody);
  }

})(inputs, outputs);

