sys_variable_value.value:
(function execute(inputs, outputs) {

  var responseBodyObject = JSON.parse(inputs.responseBody);

  if (responseBodyObject && responseBodyObject.data.length) {
    var rpIdArray = [];
    var deletedRPCount = 0;

    responseBodyObject.data.forEach(function(rp) {
        rpIdArray.push(rp.id);
    });

    var grRestorePoint = new GlideRecord('x_tesgh_veeam_app_restore_points');
    grRestorePoint.addEncodedQuery('idNOT IN' + rpIdArray.join(','));
    grRestorePoint.query();
    if (grRestorePoint.hasNext()) {
      deletedRPCount = grRestorePoint.getRowCount();
      grRestorePoint.deleteMultiple();
    }

    outputs.deleted_restore_point_count = deletedRPCount;
  }

})(inputs, outputs);

