sys_variable_value.value:
(function execute(inputs, outputs) {

  var responseBodyObject = JSON.parse(inputs.responseBody);

  if (responseBodyObject && responseBodyObject.data.length) {
    var jobArray = [];

    responseBodyObject.data.forEach(function(job) {
    	jobArray.push(job);
    });

    var importJson = {"records": jobArray};

    var request = new sn_ws.RESTMessageV2();
    request.setEndpoint(gs.getProperty('glide.servlet.uri') + 'api/now/import/x_tesgh_veeam_app_import_restore_points/insertMultiple');
    request.setHttpMethod('POST');
    request.setAuthenticationProfile('basic', '39480e731b299910e06d520b234bcb48');
    request.setRequestHeader('Accept', 'application/json');
    request.setRequestHeader('Content-Type', 'application/json');
    request.setRequestBody(JSON.stringify(importJson));

    var response = request.execute();

    outputs.body = response.getBody();
  }

})(inputs, outputs);

